package slidingWindow;

public class LongestPalindromeSubStr {
	public static void main(String[] args) {
		System.out.println(longestPalindrome(""));
	}
	
    public static String longestPalindrome(String s) {
    	if(s.length()<=1)
    		return s;
        int start = 0, end = 0;  // To store the starting and ending indices of the longest palindrome found

        for (int i = 0; i < s.length(); i++) {
            // Expand around the center for both odd and even length palindromes
            int len1 = expandAroundCenter(s, i, i); // Odd length
            int len2 = expandAroundCenter(s, i, i + 1); // Even length

            int len = Math.max(len1, len2);

            // If a longer palindrome is found, update the start and end indices
            if (len > end - start) {
                start = i - (len - 1) / 2;
                end = i + len / 2;
            }
        }

        return s.substring(start, end + 1);
    }

    private static int expandAroundCenter(String s, int left, int right) {
        while (left >= 0 && right < s.length() && s.charAt(left) == s.charAt(right)) {
            left--;
            right++;
        }
        return right - left - 1;  // The length of the palindrome
    }

}
